package classWork;

public class SwapNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =10;
		int b = 20;
		System.out.println("A "+a);
		System.out.println("B "+b);
		a = a+b;
		b = a-b;
		a = a-b;
		System.out.println("A "+a);
		System.out.println("B "+b);
	}

}
